import React, { useEffect, useState } from 'react';
import Axios from 'axios';

function ToDoList() {
  const [tasks, setTasks] = useState([]);

  const fetchTasks = async () => {
    try {
      const { data } = await Axios.get('http://localhost:8000/api/v1/task');
      const arrayOfTask = data.data;

      setTasks(arrayOfTask); // Store the entire array of tasks
      console.log(arrayOfTask);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  return (
    <div>
      <h2>To-Do List</h2>
      <form>
        <input name='addTask'><button>addTask</button></input>
      </form>
      {tasks.map(task => (
        <p key={task.id}>{task.task}</p>
      ))}
      
    </div>
  );
}

export default ToDoList;
