import React, { useEffect, useState } from 'react';
import Axios from 'axios';
import moment from 'moment'

function ToDoList() {
  const [tasks, setTasks] = useState([]);
  const postData = { task: "" };
  const [inputData, setInputData] = useState(postData);
  const handleData = (e) => {
    setInputData({ ...inputData, [e.target.name]: e.target.value });
  }

  const todayDate = moment().format('YYYY-MM-DD');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (inputData.task.trim() !== '') {
      Axios.post('http://localhost:8000/api/v1/task', inputData)
        .then((response) => {
          console.log(response);
          // Clear the input field after successfully adding a task
          setInputData({ task: '' });
          // Fetch the updated list of tasks
          fetchTasks();
        })
        .catch((error) => {
          console.error('Error adding task:', error);
        });
    } else {
      // Handle the case where the task input is empty
      console.warn('Task cannot be empty');
    }
  };
  const fetchTasks = async () => {
    try {
      const { data } = await Axios.get('http://localhost:8000/api/v1/task');
      const arrayOfTask = data.data;

      setTasks(arrayOfTask); // Store the entire array of tasks
      console.log(arrayOfTask);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  const filteredTask = tasks.filter(task => {
    const taskDate = moment(task.created_at.slice(0, 10)).format('YYYY-MM-DD');
    return todayDate === taskDate;
  })

  const filteredPendingWork = tasks.filter(task => {
    let yesterday = moment().add(-1, 'days').format('YYYY-MM-DD');
    const taskDate = moment(task.created_at.slice(0, 10)).format('YYYY-MM-DD');
    return (yesterday === taskDate && task.status === 0);
  })
  let Todayscontent;

  if (filteredTask.length > 0) {
    Todayscontent = (
      <div>
        <h3>Today's task</h3>
        {filteredTask.map(task => (
          <>
          <input type='checkbox'></input>
          <label key={task.id}>{task.task}</label>
          </>
        ))}
      </div>
    )
  } else {
    Todayscontent = (
      <div>
        <h3>No task for today</h3>
      </div>
    )
  }

  let pendingWork;
  if (filteredPendingWork.length > 0) {
    pendingWork = (
      <div>
        <h3>Yesterday pending task</h3>
        {filteredPendingWork.map(task => {
          <li key={task.id}>task.task</li>
        })}
      </div>
    )
  } else {
    pendingWork = (
      <div>
        <h3>No pending task</h3>
      </div>
    )
  }

  const handleChangeOfCheckBox = (e) => {
    e.preventDefault();
    if (e.target.checked) {
      console.log("it is checked");
    } else {
      console.log("not checked")
    }
  }
  return (
    <div>
      <h2>To-Do List</h2>
      <input type='text' name='task' value={inputData.task} onChange={handleData}></input>
      <button onClick={handleSubmit}>addTask</button>
      <div>
        <ul>
          {Todayscontent}
        </ul>
      </div>
      <div>
        {pendingWork}
      </div>
    </div>
  );
}

export default ToDoList;
