import React, { useEffect, useState } from 'react';
import Axios from 'axios';


function ToDoList() {
  const [tasks, setTasks] = useState([]);
  const postData = {addTask : ""};
  const [inputData,setInputData] = useState(postData);
  const handleData = (e) =>{
    setInputData({...inputData, [e.target.name]:e.target.value});
  }
  const handleSubmit = (e) =>{
    e.preventDefault();
    Axios.post('http://localhost:8000/api/v1/task',inputData)
    .then((response)=>{
      console.log(response);
    })
  }
  const fetchTasks = async () => {
    try {
      const { data } = await Axios.get('http://localhost:8000/api/v1/task');
      const arrayOfTask = data.data;

      setTasks(arrayOfTask); // Store the entire array of tasks
      console.log(arrayOfTask);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  return (
    <div>
      <h2>To-Do List</h2>
        <input type='text' name='addTask' value={inputData.addTask} onChange={handleData}></input>
        <button onClick={handleSubmit}>addTask</button>
      {tasks.map(task => (
        <p key={task.id}>{task.task}</p>
      ))}
      
    </div>
  );
}

export default ToDoList;
